import React, {useContext} from 'react';
import {AuthContext} from "../../../context";

const Navbar = () => {
    const {setIsAuth} = useContext(AuthContext)

    const logout = () => {
        setIsAuth(false)
        localStorage.removeItem('auth')
    }

    return (
        <div className='navbar'>
            <button
                onClick={logout}
            >
                Выйти
            </button>
        </div>
    );
};

export default Navbar;
