import {useMemo} from "react";


export const usePaginator = (totalCount, limit) => {
    const totalPages = useMemo(() => {
        return Math.ceil(totalCount / limit)
    }, [totalCount])

    return totalPages;
}

export const usePagination = (totalCount, limit) => {
    const totalPages = usePaginator(totalCount, limit)
    const arrayPages = useMemo(() => {
        const result = []
        for (let i = 0; i < totalPages; i++) {
            result.push(i + 1)
        }
        return result;
    }, [totalPages])
    return arrayPages;
}
