import React, { useState } from 'react';
import { Table} from 'antd';
const columns = [
    {
        title: 'Id',
        dataIndex: 'id',
    },
    {
        title: 'Name',
        dataIndex: 'name',
        render: (text) => <a>{text}</a>,
    },
    {
        title: 'Email',
        dataIndex: 'email',
    },
    {
        title: 'Registration date',
        dataIndex: 'registration_date',
    },
    {
        title: 'Last login date',
        dataIndex: 'last_login_date',
    },
    {
        title: 'Status',
        dataIndex: 'status',
    },
];
const data = [
    {
        key: '1',
        id: 1,
        name: 'Edward King ${i}',
        email: 'veronik@gmail.com',
        registration_date: '${i}.11.2022',
        last_login_date: '11.${i}.2022',
        status: 'free'
    },
    {
        key: '2',
        id: 2,
        name: 'Edward King $',
        email: 'veronik@gmail.co',
        registration_date: '${i}.11.2022',
        last_login_date: '11.${i}.2022',
        status: 'free'
    },
    {
        key: '3',
        id: 3,
        name: 'Edward King',
        email: 'veronik@gmail.',
        registration_date: '${i}.11.2022',
        last_login_date: '11.${i}.2022',
        status: 'free'
    },
];



const rowSelection = {
    onChange: (selectedRowKeys, selectedRows) => {
        console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
    },
    getCheckboxProps: (record) => ({
        disabled: record.name === 'Disabled User',
        // Column configuration not to be checked
        name: record.name,
    }),
};

const TableUsers = () => {
    const selectionType = 'checkbox';
    return (
        <div>
            <Table
                rowSelection={{
                    type: selectionType,
                    ...rowSelection,
                }}
                columns={columns}
                dataSource={data}
            />
        </div>
    );
};

export default TableUsers;
