import React, {useState} from 'react';
import { Form, Input, Button, Checkbox } from 'antd';

const PostForm = ({create}) => {

    const [post, setPost] = useState({id: Date.now(), title: '', body: ''});

    const addNewPost = (e) => {
        e.preventDefault();
        const newPost = {
            ...post, id: Date.now()
        }
        create(newPost);
        setPost({title: '', body: ''});
    }

    return (
        <div>
{/*            <form>
                <input
                    value={post.title}
                    onChange={(e) => setPost({...post, title: e.target.value})}
                    type="text"
                    placeholder="Название"
                />
                <input
                    value={post.body}
                    onChange={(e) => setPost({...post, body: e.target.value})}
                    type="text"
                    placeholder="Содержание"
                    />
                <button onClick={addNewPost}>Создать пост</button>
            </form>*/}

            <Form>
                <Form.Item>
                    <Input
                        value={post.title}
                        onChange={(e) => setPost({...post, title: e.target.value})}
                        type="text"
                        placeholder="Название"
                    />
                </Form.Item>

                <Form.Item>
                    <Input
                        value={post.body}
                        onChange={(e) => setPost({...post, body: e.target.value})}
                        type="text"
                        placeholder="Содержание"
                    />
                </Form.Item>

                <Form.Item
                    wrapperCol={{
                        offset: 8,
                        span: 16,
                    }}
                >
                    <Button onClick={addNewPost} type="primary" htmlType="submit">
                        Создать пост
                    </Button>
                </Form.Item>
            </Form>



        </div>
    );
};

export default PostForm;
