import React, {useState} from 'react';
import Axios from 'axios';
import { Form, Input, Button, Checkbox } from 'antd';

const register = () => {
    Axios.post('https://localhost')
}

const Registration = ({create}) => {

    const [usernameReg, setUsernameReg] = useState('');
    const [emailReg, setEmailReg] = useState('');
    const [passwordReg, setPasswordReg] = useState('');
    const [user, setUser] = useState({
        key: 1,
        id: 1,
        name: '',
        email: '',
        password: '',
        registration_date: Date.now(),
        last_login_date: '11.${i}.2022',
        status: 'free'
    });

    const addNewUser = (e) => {
        e.preventDefault();
        const newUser = {
            ...user, key: user.key, id: user.id, registration_date: new Date(Date.now()).toLocaleString().split(',')[0], last_login_date: new Date(Date.now()).toLocaleString().split(',')[0], status: 'free'
        }
        create(newUser);
        setUser({key: user.key + 1, id: user.id + 1,name: '', email: '', password: ''});
    }


    return (
        <div>
        <Form>
            <Form.Item
                label="Username"
                rules={[
                    {
                        required: true,
                        message: 'Please input your username!',
                    },
                ]}
            >
                <Input
                    onChange={(e) => setUsernameReg(e.target.value)}
                    type="text"
                />
            </Form.Item>

            <Form.Item
                label="Email"
                rules={[
                    {
                        required: true,
                        message: 'Please input your email!',
                    },
                ]}
            >
                <Input
                    onChange={(e) => setEmailReg(e.target.value)}
                    type="text"
                />
            </Form.Item>

            <Form.Item
                label="Password"
                rules={[
                    {
                        required: true,
                        message: 'Please input your password!',
                    },
                ]}
            >
                <Input.Password
                    value={user.password}
                    onChange={(e) => setPasswordReg(e.target.value)}
                    type="text"
                />
            </Form.Item>

            <Form.Item
                valuePropName="checked"
                wrapperCol={{
                    offset: 8,
                    span: 16,
                }}
            >
                <Checkbox>Remember me</Checkbox>
            </Form.Item>

            <Form.Item
                wrapperCol={{
                    offset: 8,
                    span: 16,
                }}
            >
                <Button onClick={addNewUser} type="primary" htmlType="submit">
                    Submit
                </Button>
            </Form.Item>
        </Form>
        </div>
    );
};

export default Registration;
