import React, {useState} from 'react';
import { Form, Input, Button, Checkbox } from 'antd';

const Registration = ({create}) => {

    const [user, setUser] = useState({
        key: 1,
        id: 1,
        name: '',
        email: '',
        password: '',
        registration_date: Date.now(),
        last_login_date: '11.${i}.2022',
        status: 'free'
    });

    const addNewUser = (e) => {
        e.preventDefault();
        const newUser = {
            ...user, key: 1, id: 1, registration_date: Date.now(), last_login_date: '11.${i}.2022', status: 'free'
        }
        create(newUser);
        setUser({name: '', email: '', password: ''});
    }


    return (
        <div>
        <Form>
            <Form.Item
                label="Username"
                rules={[
                    {
                        required: true,
                        message: 'Please input your username!',
                    },
                ]}
            >
                <Input
                    value={user.name}
                    onChange={(e) => setUser({...user, name: e.target.value})}
                    type="text"
                />
            </Form.Item>

            <Form.Item
                label="Email"
                rules={[
                    {
                        required: true,
                        message: 'Please input your email!',
                    },
                ]}
            >
                <Input
                    value={user.email}
                    onChange={(e) => setUser({...user, email: e.target.value})}
                    type="text"
                />
            </Form.Item>

            <Form.Item
                label="Password"
                rules={[
                    {
                        required: true,
                        message: 'Please input your password!',
                    },
                ]}
            >
                <Input.Password
                    value={user.password}
                    onChange={(e) => setUser({...user, password: e.target.value})}
                    type="text"
                />
            </Form.Item>

            <Form.Item
                valuePropName="checked"
                wrapperCol={{
                    offset: 8,
                    span: 16,
                }}
            >
                <Checkbox>Remember me</Checkbox>
            </Form.Item>

            <Form.Item
                wrapperCol={{
                    offset: 8,
                    span: 16,
                }}
            >
                <Button onClick={addNewUser} type="primary" htmlType="submit">
                    Submit
                </Button>
            </Form.Item>
        </Form>
        </div>
    );
};

export default Registration;
